import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setTasks, addTask, updateTask, deleteTask } from '../features/tasks/tasksSlice';
import { fetchTasks, createTask, editTask, removeTask } from '../services/taskService';
import TaskItem from './TaskItem';
import AddTaskForm from './AddTaskForm';
import ProgressBar from './ProgressBar';
import ConfirmationDialog from './ConfirmationDialog';

const TaskList = () => {
    const [showAddForm, setShowAddForm] = useState(false);
    const [showConfirmation, setShowConfirmation] = useState(false);
    const [selectedTask, setSelectedTask] = useState(null);
    const [taskPriority, setTaskPriority] = useState('Low');
    const [editingTask, setEditingTask] = useState(null);
    const tasks = useSelector((state) => state.tasks);
    const dispatch = useDispatch();

    useEffect(() => {
        const initializeTasks = async () => {
            const fetchedTasks = await fetchTasks(); // API call
            dispatch(setTasks(fetchedTasks)); // Redux action
        };
        initializeTasks();
    }, [dispatch]);

    const handleAddTask = async (taskName) => {
        if (editingTask) {
            const updatedTask = { ...editingTask, name: taskName, priority: taskPriority };
            const result = await editTask(updatedTask.id, updatedTask); // API call
            dispatch(updateTask(result)); // Redux action
        } else {
            const newTask = { name: taskName, priority: taskPriority, status: 'To Do' };
            const addedTask = await createTask(newTask); // API call
            dispatch(addTask(addedTask)); // Redux action
        }
        setShowAddForm(false);
    };


    const handleEdit = (task) => {
        setEditingTask(task);
        setShowAddForm(true);
    };

    const handleDelete = (task) => {
        setSelectedTask(task);
        setShowConfirmation(true);
    };

    const confirmDelete = () => {
        deleteTask(selectedTask.id);
        setShowConfirmation(false);
        setSelectedTask(null);
    };

    const handleStatusChange = (taskId, newStatus) => {
        const task = tasks.find(t => t.id === taskId);
        if (task) {
            updateTask({ ...task, status: newStatus });
        }
    };

    const completedTasks = tasks.filter(task => task.status === 'Completed').length;

    return (
        <div>
            <ProgressBar completedCount={completedTasks} totalCount={tasks.length} />
            <button onClick={handleAddTaskClick}>Add Task</button>

            {showAddForm && <AddTaskForm onAdd={handleAddTask} setPriority={setTaskPriority} task={editingTask} />}

            <ul>
                {tasks.map(task => (
                    <li key={task.id}>
                        <TaskItem task={task} onStatusChange={handleStatusChange} />
                        <button onClick={() => handleEdit(task)}>Edit</button>
                        <button onClick={() => handleDelete(task)}>Delete</button>
                        <span>Priority: {task.priority}</span>
                    </li>
                ))}
            </ul>

            {showConfirmation && (
                <ConfirmationDialog
                    isOpen={showConfirmation}
                    onClose={() => setShowConfirmation(false)}
                    onConfirm={confirmDelete}
                    message="Are you sure you want to delete this task?"
                />
            )}
        </div>
    );
};

export default TaskList;
