import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addTaskAction } from '../features/tasks/tasksSlice';
import { addTask } from '../services/taskService';
import { Button, TextField, FormControl, FormLabel, RadioGroup, FormControlLabel, Radio } from '@mui/material';

const AddTaskForm = ({ setPriority }) => {
    const [taskName, setTaskName] = useState('');
    const [localPriority, setLocalPriority] = useState('Low');
    const dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newTask = { name: taskName, priority: localPriority, status: 'To Do' };
        const addedTask = await addTask(newTask); // API call
        dispatch(addTaskAction(addedTask)); // Redux action
        setTaskName('');
        if (setPriority) {
            setPriority(localPriority);
        }
    };

    const handlePriorityChange = (event) => {
        setLocalPriority(event.target.value);
    };

    return (
        <form onSubmit={handleSubmit}>
            <TextField 
                label="New Task" 
                variant="outlined"
                value={taskName} 
                onChange={(e) => setTaskName(e.target.value)}
                fullWidth
                margin="normal"
            />

            <FormControl component="fieldset" margin="normal">
                <FormLabel component="legend">Priority</FormLabel>
                <RadioGroup row value={localPriority} onChange={handlePriorityChange}>
                    <FormControlLabel value="High" control={<Radio />} label="High" />
                    <FormControlLabel value="Medium" control={<Radio />} label="Medium" />
                    <FormControlLabel value="Low" control={<Radio />} label="Low" />
                </RadioGroup>
            </FormControl>

            <Button type="submit" variant="contained" color="primary" fullWidth>
                Add Task
            </Button>
        </form>
    );
};

export default AddTaskForm;
