import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchTasksAction, deleteTaskAction } from '../features/tasks/tasksSlice';
import { deleteTask } from '../services/taskService';
import TaskItem from './TaskItem';
import AddTaskForm from './AddTaskForm';
import EditTaskForm from './EditTaskForm';
import ConfirmationDialog from './ConfirmationDialog';
import { 
    Button, 
    Container, 
    Card, 
    CardContent, 
    Typography, 
    Dialog, 
    DialogActions, 
    DialogContent, 
    DialogContentText, 
    DialogTitle,
    Grid
}  from '@mui/material';


const TaskList = () => {
    // Assuming the state structure is { tasks: { tasks: [] } }
    const tasks = useSelector((state) => state.tasks.tasks);
    const dispatch = useDispatch();

    const [showAddForm, setShowAddForm] = useState(false);
    const [showEditForm, setShowEditForm] = useState(false);
    const [currentTask, setCurrentTask] = useState(null);
    const [showConfirmDialog, setShowConfirmDialog] = useState(false);
    const [priority, setPriority] = useState('Low');

    useEffect(() => {
        dispatch(fetchTasksAction());
    }, [dispatch]);

   

    return (
        <Container sx={{ 
            backgroundColor: '#f0f2f5' ,
           // Adjust for more roundness
            width: '90%', // Adjust the width as per your requirement
            maxWidth: '700px', // Maximum width, adjust as needed
      // Centers the card if width is less than 100%

          }}> {/* Adjust the color as needed */}
        <Grid container justifyContent="space-between" alignItems="center" sx={{ marginBottom: 2 ,  width: '90%',   margin: 'auto', 
            maxWidth: '700px',}}>
            <Typography variant="h4"  >Task List</Typography>
            <Button variant="contained" color="primary" onClick={() => setShowAddForm(!showAddForm)}>
                + ADD Task
            </Button>
        </Grid>

        {showAddForm && <AddTaskForm setPriority={setPriority} />}
        {showEditForm && <EditTaskForm task={currentTask} setPriority={setPriority} />}

        {tasks.map((task) => (
           <Card 
           key={task.id} 
           variant="outlined" 
           sx={{ 
             backgroundColor: 'white', 
             borderRadius: '16px', // Adjust for more roundness
             width: '90%', // Adjust the width as per your requirement
             maxWidth: '700px', // Maximum width, adjust as needed
             margin: 'auto', // Centers the card if width is less than 100%
             marginBottom: 2 
           }}
         >
                <CardContent>
                    <TaskItem task={task} />
                 
                </CardContent>
            </Card>
        ))}

            {/* Confirmation Dialog */}
            <Dialog open={showConfirmDialog} onClose={() => setShowConfirmDialog(false)}>
                <DialogTitle>{"Confirm Deletion"}</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Are you sure you want to delete this task?
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setShowConfirmDialog(false)} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={() => handleDelete(currentTask)} color="secondary" autoFocus>
                        Confirm
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default TaskList;
