import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateTaskAction } from '../features/tasks/tasksSlice';
import { updateTask } from '../services/taskService';
import ProgressBar from './ProgressBar';
const TaskItem = ({ task }) => {
    const dispatch = useDispatch();
    const [progressBarStatus, setProgressBarStatus] = useState(task.status || 'ToDo');

    const getPriorityColor = (priority) => {
        switch(priority) {
            case 'High': return 'red';
            case 'Medium': return 'orange';
            case 'Low': return 'green';
            default: return 'black';
        }
    };

    const handleStatusChange = async (newStatus) => {
        const updatedTask = { ...task, status: newStatus };
        try {
            const result = await updateTask(task.id, updatedTask); // API call
            dispatch(updateTaskAction(result)); // Redux action
        } catch (error) {
            console.error('Error updating task status:', error);
        }
    };

    const handleProgressBarClick = async () => {
        const statuses = ['ToDo', 'InProgress', 'Completed'];
        const nextIndex = (statuses.indexOf(progressBarStatus) + 1) % statuses.length;
        const newStatus = statuses[nextIndex];
        setProgressBarStatus(newStatus);
        await handleStatusChange(newStatus);
    };

    return (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <span>{task.name}</span>
            <span style={{ color: getPriorityColor(task.priority) }}>{task.priority}</span>
            <ProgressBar status={progressBarStatus} onClick={handleProgressBarClick} />
            <button onClick={() => handleStatusChange('To Do')}
                style={{ backgroundColor: task.status === 'To Do' ? 'lightgray' : 'transparent' }}>
                To Do
            </button>
            <button onClick={() => handleStatusChange('In Progress')}
                style={{ backgroundColor: task.status === 'In Progress' ? 'lightgray' : 'transparent' }}>
                In Progress
            </button>
            <button onClick={() => handleStatusChange('Completed')}
                style={{ backgroundColor: task.status === 'Completed' ? 'lightgray' : 'transparent' }}>
                Completed
            </button>
        </div>
    );
};

export default TaskItem;
