import React from 'react';
import LinearProgress from '@mui/material/LinearProgress';
import Box from '@mui/material/Box';

const ProgressBar = ({ status, onClick }) => {
    let value;

    switch (status) {
        case 'Completed':
            value = 100;
            break;
        case 'InProgress':
            value = 50;
            break;
        case 'ToDo':
        default:
            value = 0;
            break;
    }

    return (
        <Box sx={{ width: '100%', cursor: 'pointer' }} onClick={onClick}>
            <LinearProgress variant="determinate" value={value} 
                            sx={{ 
                                height: 20, 
                                '& .MuiLinearProgress-bar': {
                                    backgroundColor: status === 'Completed' ? 'green' : (status === 'InProgress' ? 'grey' : 'lightgrey')
                                }
                            }} 
            />
        </Box>
    );
};

export default ProgressBar;
