import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addTask } from '../services/taskService';

const AddTaskForm = ({ onAdd, setPriority, task }) => {
    const [taskName, setTaskName] = useState('');
    const [localPriority, setLocalPriority] = useState('Low');    
    const dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newTask = { name: taskName, priority: localPriority, status: 'To Do' };
        const addedTask = await createTask(newTask); // API call
        dispatch(addTask(addedTask)); // Redux action
        setTaskName('');
        if (globalSetPriority) {
            globalSetPriority(localPriority);
        }
    };

    const handlePriorityChange = (priority) => {
        setLocalPriority(priority);
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                value={taskName} 
                onChange={(e) => setTaskName(e.target.value)}
                placeholder="New task"
            />
            <div>
                <button type="button" onClick={() => handlePriorityChange('High')}>High</button>
                <button type="button" onClick={() => handlePriorityChange('Medium')}>Medium</button>
                <button type="button" onClick={() => handlePriorityChange('Low')}>Low</button>
            </div>
            <button type="submit">Add Task</button>
        </form>
    );
};

export default AddTaskForm;
