import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchTasksAction, deleteTaskAction } from '../features/tasks/tasksSlice';
import { deleteTask } from '../services/taskService';
import TaskItem from './TaskItem';
import AddTaskForm from './AddTaskForm';
import EditTaskForm from './EditTaskForm';
import ConfirmationDialog from './ConfirmationDialog';
import { 
    Button, 
    Container, 
    Card, 
    CardContent, 
    Typography, 
    Dialog, 
    DialogActions, 
    DialogContent, 
    DialogContentText, 
    DialogTitle,
    Grid
}  from '@mui/material';


const TaskList = () => {
    // Assuming the state structure is { tasks: { tasks: [] } }
    const tasks = useSelector((state) => state.tasks.tasks);
    const dispatch = useDispatch();

    const [showAddForm, setShowAddForm] = useState(false);
    const [showEditForm, setShowEditForm] = useState(false);
    const [currentTask, setCurrentTask] = useState(null);
    const [showConfirmDialog, setShowConfirmDialog] = useState(false);
    const [priority, setPriority] = useState('Low');

    useEffect(() => {
        dispatch(fetchTasksAction());
    }, [dispatch]);

    const handleDelete = async (task) => {
        try {
            await deleteTask(task.id);
            dispatch(deleteTaskAction(task.id));
        } catch (error) {
            console.error('Failed to delete task:', error);
        } finally {
            setShowConfirmDialog(false);
        }
    };

    const confirmDelete = (task) => {
        setCurrentTask(task);
        setShowConfirmDialog(true);
    };

    const handleEditClick = (task) => {
        setCurrentTask(task);
        setShowEditForm(true);
    };

    return (
        <Container sx={{ backgroundColor: '#003366', padding: 4, borderRadius: 2 }}> {/* Adjust the color as needed */}
        <Grid container justifyContent="space-between" alignItems="center" sx={{ marginBottom: 2 }}>
            <Typography variant="h4" sx={{ color: 'white' }}>Task List</Typography>
            <Button variant="contained" color="primary" onClick={() => setShowAddForm(!showAddForm)}>
                + ADD Task
            </Button>
        </Grid>

        {showAddForm && <AddTaskForm setPriority={setPriority} />}
        {showEditForm && <EditTaskForm task={currentTask} setPriority={setPriority} />}

        {tasks.map((task) => (
            <Card key={task.id} variant="outlined" sx={{ backgroundColor: 'white', borderRadius: 2, marginBottom: 2 }}>
                <CardContent>
                    <TaskItem task={task} />
                    <Button variant="contained" color="secondary" onClick={() => handleEditClick(task)}>Edit</Button>
                    <Button variant="outlined" color="error" onClick={() => confirmDelete(task)}>Delete</Button>
                </CardContent>
            </Card>
        ))}

            {/* Confirmation Dialog */}
            <Dialog open={showConfirmDialog} onClose={() => setShowConfirmDialog(false)}>
                <DialogTitle>{"Confirm Deletion"}</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Are you sure you want to delete this task?
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setShowConfirmDialog(false)} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={() => handleDelete(currentTask)} color="secondary" autoFocus>
                        Confirm
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default TaskList;
