import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { updateTaskAction } from '../features/tasks/tasksSlice';
import { updateTask } from '../services/taskService';

const EditTaskForm = ({ task, setPriority }) => {
    const [taskName, setTaskName] = useState(task.name);
    const [currentPriority, setCurrentPriority] = useState(task.priority);
    const dispatch = useDispatch();

    useEffect(() => {
        setTaskName(task.name);
        setCurrentPriority(task.priority);
    }, [task]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const updatedTask = { ...task, name: taskName, priority: currentPriority };
        const result = await updateTask(task.id, updatedTask); // API call
        dispatch(updateTaskAction(result)); // Redux action
    };

    const handlePriorityChange = (priority) => {
        setCurrentPriority(priority);
        if (setPriority) {
            setPriority(priority);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                value={taskName} 
                onChange={(e) => setTaskName(e.target.value)}
                placeholder="Edit task"
            />
            <div>
                <button type="button" onClick={() => handlePriorityChange('High')}>High</button>
                <button type="button" onClick={() => handlePriorityChange('Medium')}>Medium</button>
                <button type="button" onClick={() => handlePriorityChange('Low')}>Low</button>
            </div>
            <button type="submit">Update Task</button>
        </form>
    );
};

export default EditTaskForm;
